from user

  
    
      AND user_id =   #{userId,jdbcType=VARCHAR}
    
    
      AND  nick_name  like CONCAT('%', #{nickName,jdbcType=VARCHAR},%)
    
    
      AND  avatar_url = #{avatarUrl,jdbcType=VARCHAR}
    
    
